mapper.update:
update d_body_diet
u_id = #{uId,jdbcType=BIGINT}, meal = #{meal,jdbcType=VARCHAR}, picture = #{picture,jdbcType=VARCHAR}, food_name = #{foodName,jdbcType=VARCHAR}, details = #{details,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, status = #{status,jdbcType=TINYINT}, create_time = #{createTime,jdbcType=TIMESTAMP}, created = #{created,jdbcType=DATE},
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, u_id, meal, nullif(concat('${httpUrl}',picture), '${httpUrl}') as picture,
food_name, details, description, status, create_time, created from d_body_diet

  and u_id = #{uId}
  and meal = #{meal}
  and created = #{created}
  and status = 1

order by create_time desc
