mapper.update:
update d_body_drug
u_id = #{uId,jdbcType=BIGINT}, measure = #{measure,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, status = #{status,jdbcType=TINYINT}, create_time = #{createTime,jdbcType=TIMESTAMP}, created = #{created,jdbcType=DATE},
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, u_id, measure, name, status, create_time, created from d_body_drug

  and u_id = #{uId}
  and created = #{created}
  and status = 1

order by create_time desc
