mapper.update:
update b_drug
drug_name = #{drugName,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, drug_type = #{drugType,jdbcType=TINYINT}, status = #{status,jdbcType=TINYINT}, create_time = #{createTime,jdbcType=TIMESTAMP},
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, drug_name, name, drug_type, status, create_time from b_drug

  and drug_name = #{drugName}
  and name like concat(#{name},'%')
  and drug_type = #{drugType}
  and status = 1
